let weeks = [];

function weekBounds(year, weekNumber) {
  const monday = DateTime.fromObject({
    weekYear: year,
    weekNumber: weekNumber,
    weekday: 1,   // Monday
    hour: 0,
    minute: 0,
    second: 0,
    millisecond: 0
  });

  const sunday = monday.plus({ days: 6 });

  return {
    start: monday.toFormat("MM/dd/yyyy"),
    end: sunday.toFormat("MM/dd/yyyy")
  };
}

for (const item of $input.all()) {
  let currentWeek = DateTime.now().weekNumber;
  let currentYear = 2025;

  while(weeks.length < 4) {
    if (currentWeek > 52) {
      currentWeek = 1;
      currentYear++;
    }
    if ($input.first().json.data.find(previous => previous?.week === `${currentYear}/${currentWeek}`)) {
      currentWeek++;
      continue;
    }
    const {start, end} = weekBounds(currentYear, currentWeek)
    weeks.push({
      currentYear,
      currentWeek,
      startOfWeek: start,
      endOfWeek: end
    });
    currentWeek++;
  }
}

return [{
  json: {
    weeks
  }
}]

